import{_ as e,c as o,o as d,b as t}from"./app.1e54b5c6.js";const f=JSON.parse('{"title":"js\u548Cnode\u4E2D\u7684\u6A21\u5757\u7CFB\u7EDF","description":"","frontmatter":{"title":"js\u548Cnode\u4E2D\u7684\u6A21\u5757\u7CFB\u7EDF"},"headers":[],"relativePath":"book/patterns/module-system/module-system-in-js-and-node.md","lastUpdated":1666853863000}'),r={name:"book/patterns/module-system/module-system-in-js-and-node.md"},n=t('<p>\u4E0D\u662F\u6240\u6709\u7F16\u7A0B\u8BED\u8A00\u90FD\u5B58\u5728\u5185\u7F6E\u7684\u6A21\u5757\u7CFB\u7EDF\uFF0CJS\u5F88\u957F\u4E00\u6BB5\u65F6\u95F4\u662F\u7F3A\u4E4F\u8FD9\u4E2A\u529F\u80FD\u7684\u{1F605}\u3002</p><p>\u5728\u6D4F\u89C8\u5668\u4E2D\uFF0C\u53EF\u4EE5\u5C06\u4EE3\u7801\u62C6\u5206\u4E3A\u591A\u4E2A\u90E8\u5206\uFF0C\u7136\u540E\u901A\u8FC7\u4E0D\u540C\u7684 <code>&lt;script&gt;</code> \u6807\u7B7E\u7684\u65B9\u5F0F\u5BFC\u5165\u3002\u5BF9\u4E8E\u7B80\u5355\u7684\u5E94\u7528\u8FD9\u4E2A\u8FD8\u884C\uFF0C\u4F46\u662FJS\u5F00\u53D1\u8005\u4E00\u76F4\u7F3A\u5C11\u529F\u80FD\u5B8C\u5584\u7684\u6A21\u5757\u7CFB\u7EDF\u3002</p><p>\u5F53JS\u6D4F\u89C8\u5668\u5E94\u7528\u53D8\u5F97\u8D8A\u6765\u8D8A\u590D\u6742\uFF0C\u50CF <code>jQuery</code>, <code>Backbone</code>, <code>AngularJS</code> \u8FD9\u6837\u7684\u6846\u67B6\u5360\u636E\u6D4F\u89C8\u5668\u751F\u6001\u65F6\uFF0CJS\u793E\u533A\u63D0\u51FA\u4E86\u51E0\u4E2A\u8BA1\u5212\uFF0C\u65E8\u5728\u5B9A\u4E49\u4E00\u4E2A\u53EF\u4EE5\u5728JavaScript\u9879\u76EE\u4E2D\u6709\u6548\u91C7\u7528\u7684\u6A21\u5757\u7CFB\u7EDF\u3002\u6700\u6210\u529F\u7684\u662F <code>AMD(Asynchronous Module Definition)</code>\uFF0C\u5B83\u56E0 <a href="http://nodejsdp.link/requirejs" target="_blank" rel="noreferrer">RequireJS</a> \u800C\u6D41\u884C\uFF0C\u548C\u4E4B\u540E\u7684 <a href="http://nodejsdp.link/umd" target="_blank" rel="noreferrer">UMD</a>(Universal Module Definition)\u3002</p><p>\u5F53Node.js\u88AB\u521B\u5EFA\u65F6\uFF0C\u5B83\u88AB\u8BA4\u4E3A\u662F\u4E00\u79CD <code>JS\u670D\u52A1\u7AEF\u8FD0\u884C\u65F6</code>\uFF0C\u80FD\u76F4\u63A5\u8BBF\u95EE\u5E95\u5C42\u6587\u4EF6\u7CFB\u7EDF\uFF0C\u56E0\u6B64\uFF0C\u5F15\u5165\u4E00\u79CD\u4E0D\u540C\u7684\u6A21\u5757\u7BA1\u7406\u65B9\u5F0F\u662F\u4E00\u4E2A\u72EC\u7279\u7684\u673A\u4F1A\u3002\u4E3B\u4F53\u601D\u60F3\u662F\uFF0C\u4E0D\u4F9D\u8D56HTML <code>&lt;script&gt;</code> \u6807\u7B7E\u548C\u901A\u8FC7 <code>URLs</code> \u7684\u8D44\u6E90\u8BBF\u95EE\u3002\u76F8\u53CD\uFF0C\u9009\u62E9\u5B8C\u5168\u4F9D\u8D56\u672C\u5730\u6587\u4EF6\u7CFB\u7EDF\u4E0A\u53EF\u7528\u7684JavaScript\u6587\u4EF6\u3002\u4F5C\u4E3A\u5176\u6A21\u5757\u7CFB\u7EDF\uFF0CNode.js\u60F3\u5230\u4E86\u5BF9 <code>CommonJS</code> \u89C4\u8303\u7684\u5B9E\u73B0\uFF08\u4E5F\u79F0\u4E4B\u4E3A <a href="http://nodejsdp.link/commonjs" target="_blank" rel="noreferrer">CJS</a>\uFF09\uFF0C\u7528\u4E8E\u7ED9<code>\u975E\u6D4F\u89C8\u5668\u73AF\u5883</code>\u7684JS\u63D0\u4F9B\u4E00\u4E2A\u6A21\u5757\u7CFB\u7EDF\u{1F60E}\u3002</p><p>CommonJS\u81EA\u5176\u53D1\u5E03\u8D77\uFF0C\u5C31\u6210\u4E3A\u4E86Node.js\u4E2D\u7EDF\u6CBB\u6027\u7684\u6A21\u5757\u7CFB\u7EDF\uFF0C\u540C\u65F6\u7531\u4E8E\u50CF<a href="http://nodejsdp.link/browserify" target="_blank" rel="noreferrer">Browserify</a> \u548C <a href="http://nodejsdp.link/webpack" target="_blank" rel="noreferrer">Webpack</a> \u8FD9\u6837\u7684 <code>\u6A21\u5757\u6253\u5305\u5668</code>\u7684\u51FA\u73B0\uFF0C\u4F7F\u5176\u5728\u6D4F\u89C8\u5668\u7AEF\u4E5F\u53D8\u5F97\u5F88\u6D41\u884C\u8D77\u6765\u3002</p><p>\u{1F389}\u57282015\u5E74\uFF0C\u968F\u7740ES2015\u7684\u53D1\u5E03\uFF0C\u7EC8\u4E8E\u51FA\u73B0\u4E86\u5B98\u65B9\u63D0\u51FA\u7684\u6807\u51C6\u7684\u6A21\u5757\u7CFB\u7EDF\u8BF7\u6C42\uFF1A<code>ESM</code> \u6216 <code>ES Modules</code>\u3002ESM\u7ED9JS\u751F\u6001\u5E26\u6765\u4E86\u5F88\u591A\u521B\u65B0\uFF0C\u8FD9\u5176\u4E2D\uFF0C\u5B83\u5C1D\u8BD5\u586B\u8865\u670D\u52A1\u7AEF\u548C\u6D4F\u89C8\u5668\u7AEF\u7684\u6A21\u5757\u7BA1\u7406\u7684\u95F4\u9699\uFF0C\u4F7F\u5176\u7EDF\u4E00\u3002</p><p>ECMAScript 6\u4EC5\u5728\u8BED\u6CD5\u548C\u8BED\u4E49\u65B9\u9762\u5B9A\u4E49\u4E86ESM\u7684\u6B63\u5F0F\u89C4\u8303\uFF0C\u5E76\u6CA1\u6709\u63D0\u4F9B\u4EFB\u4F55\u5B9E\u73B0\u7EC6\u8282\u3002Node.js\u793E\u533A\u548C\u6D4F\u89C8\u5668\u516C\u53F8\u82B1\u8D39\u4E86\u597D\u51E0\u5E74\u5BF9\u89C4\u8303\u8FDB\u884C\u4E86\u5B9E\u73B0\u3002Node.js\u4ECE <code>V13.2</code> \u5C01\u88C5\u4E86\u5BF9ESM\u7684\u7A33\u5B9A\u652F\u6301\u3002</p><p>ESM\u5C06\u6210\u4E3A\u7BA1\u7406\u6D4F\u89C8\u5668\u7AEF\u548C\u670D\u52A1\u5668\u7AEFJS\u6A21\u5757\u7684\u7EDD\u5BF9\u4E8B\u5B9E\u65B9\u5F0F\u3002\u4F46\u4ECD\u9700\u8981\u65F6\u95F4\u4ECECommonJS\u5411ESM\u6807\u51C6\u8FDB\u884C\u8FC7\u5EA6\u3002</p><p>\u4E3A\u4E86\u63D0\u4F9BNode.js\u4E2D\u6A21\u5757\u76F8\u5173\u7684\u7EFC\u5408\u6027\u6982\u89C8\uFF0C\u672C\u7AE0\u7684\u7B2C\u4E00\u90E8\u5206\uFF0C\u5C06\u5728CommonJS\u7684\u4E0A\u4E0B\u6587\u4E2D\u8FDB\u884C\u8BA8\u8BBA\u3002\u7136\u540E\uFF0C\u7B2C\u4E8C\u90E8\u5206\u5C06\u4F7F\u7528ESM\u3002</p><p>\u672C\u7AE0\u7684\u76EE\u7684\u662F\uFF0C\u4F7F\u4F60\u80FD\u540C\u65F6\u9002\u5E94\u8FD92\u79CD\u6A21\u5757\u7CFB\u7EDF\u{1F60E}\uFF0C\u4F46\u662F<code>\u672C\u4E66\u540E\u9762\u7684\u5185\u5BB9\uFF0C\u5C06\u53EA\u4F7F\u7528ESM\u4F5C\u4E3A\u4EE3\u7801\u6F14\u793A</code>\u3002\u8FD9\u66F4\u52A0\u9762\u5411\u672A\u6765\u3002</p><p>\u7406\u89E3CommonJS\u7684\u7279\u8D28\u80FD\u5E2E\u52A9\u6211\u4EEC\u66F4\u597D\u7684\u7406\u89E3ESM\uFF0C\u548CESM\u7684\u4F18\u52BF\u3002</p><p>2022\u5E7409\u670812\u65E523:53:30</p>',12),s=[n];function c(a,p,l,S,i,_){return d(),o("div",null,s)}const J=e(r,[["render",c]]);export{f as __pageData,J as default};
